'');add_option('cc-sitemap-categories', '');add_option('cc-sitemap-post', '');}function deactive_sitemap() {delete_option('cc-sitemap');delete_option('cc-sitemap-page');delete_option('cc-sitemap-categories');delete_option('cc-sitemap-post');}register_activation_hook(__FILE__, 'activate_sitemap');register_deactivation_hook(__FILE__, 'deactive_sitemap');function register_cc_sitemap_settings() {register_setting('cc-sitemap-option-group', 'cc-sitemap');register_setting('cc-sitemap-option-group', 'cc-sitemap-page');register_setting('cc-sitemap-option-group', 'cc-sitemap-categories');register_setting('cc-sitemap-option-group', 'cc-sitemap-post');}// Display the page content for the login logo submenufunction cc_sitemapwidget_settings() {include(WP_PLUGIN_DIR . '/sitemap-widget/options.php');}/* Reference CSS File */function sitemap_by_cc() {echo '<link rel="stylesheet" type="text/css" href="' . plugins_url('sitemap.css', __FILE__) . '" />';}add_action('login_head', 'sitemap_by_cc');/* Show Logo */function cc_sitemap() {$ccsitemap = get_option('cc-sitemap');if (!empty($ccsitemap)) {function cc_sitemap_widget_function() {_e( "Hello, to visit a category,page or post, just click on it!",'sitemap-widget');?><div style="overflow:auto">    <table>        <tbody>            <tr>          <?php $ccsitemapcategories = get_option('cc-sitemap-categories');        if (!empty($ccsitemapcategories)) {;        ?>                                        <th><h2><?php _e('Categories', 'sitemap-widget'); ?></h2></th>        <?php }; ?>        <?php $ccsitemappage = get_option('cc-sitemap-page');        if (!empty($ccsitemappage)) {;        ?>         <th><h2><?php _e('Pages', 'sitemap-widget'); ?></h2></th>        <?php }; ?>         <?php $ccsitemappost = get_option('cc-sitemap-post');        if (!empty($ccsitemappost)) {;        ?>         <th><h2><?php _e('Posts', 'sitemap-widget'); ?></h2></th>                    <?php }; ?>        </tr>                    <?php if (!empty($ccsitemapcategories)) {; ?>          <td id="categories">            <form action="<?php bloginfo('url'); ?>" method="get">                <div><?php$select = wp_dropdown_categories('hierarchical=0&show_count=1&orderby=name&echo=0&taxonomy=category');$select = preg_replace("#<select([^>]*)>#", "<select$1 onchange='return this.form.submit()'>", $select);echo $select;?>                    <noscript><div><input type="submit" value="View" /></div></noscript>                </div></form>        </td>                    <?php }; ?>                    <?php if (!empty($ccsitemappage)) {; ?>         <td id="pages">            <form action="<?php bloginfo('url'); ?>/" method="get">                <div><?php$select = wp_dropdown_pages('show_count=1&orderby=name&echo=0&exclude=84,73');$select = preg_replace("#<select([^>]*)>#", "<select$1 onchange='return this.form.submit()'>", $select);echo $select;?>                    <noscript><div><input type="submit" value="View" /></div></noscript>                </div></form>        </td>        <?php }; ?><?php if (!empty($ccsitemappost)) {; ?>         <td><select name="archive-dropdown" onChange='document.location.href = this.options[this.selectedIndex].value;'>                <option value=""><?php echo attribute_escape(_e('Choose a post','sitemap-widget')); ?></option><?php wp_get_archives('type=postbypost&format=option&show_post_count=0'); ?> </select>        </td><?php }; ?>        </tbody>    </table></div><?php}wp_add_dashboard_widget('sitemap-widget', 'SiteMap widget', 'cc_sitemap_widget_function');}}add_action('wp_dashboard_setup', 'cc_sitemap');?>